mapper.select:
select id,author,title,description,content,status,create_time,update_time,is_deleted from sys_write
and (title like CONCAT('%',#{vo.keyword},'%') or author like CONCAT('%',#{vo.keyword},'%') or description like CONCAT('%',#{vo.keyword},'%'))
and create_time >= #{vo.createTimeBegin}
and create_time <= #{vo.createTimeEnd}
and is_deleted=0
order by id desc
